    const posts = colors.map((color)=>
        <Block color={color}></Block>
    ); // TODO: edit this variable

    return (
        <div>
            <Menu handleClick={(color) => setColor([...colors, color])}></Menu>

            {/* Below is where all of your Blocks should render! */}
            {posts}
        </div>
    );
}

